    </dl>
  {{/if}}

  {{#each product.custom_fields}}
    <dl class="product-details-block product-details-custom">
      <dt class="product-details-name">{{name}}:</dt>
      <dd class="product-details-value">{{{value}}}</dd>
    </dl>
  {{/each}}
</div>

components/common/share-buttons:
{{#if share_buttons}}
  <nav class="share-buttons-nav">
    <ul class="share-buttons" data-share-buttons>

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook_like'}}
          <li class="social-link facebook-like">
            <div class="fb-like"
              data-href="{{../../url}}"
              data-layout="button_count"
              data-action="like"
              data-show-faces="false">
            </div>
          </li>
        {{/if}}
      {{/each}}

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook'}}
          <li class="social-link facebook">
            <a target="_blank" href="http://www.facebook.com/sharer.php?u={{../../url}}" class="facebook">
              {{> svg/social/facebook}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'twitter'}}
          <li class="social-link twitter">
            <a target="_blank" href="https://twitter.com/share?url={{../../url}}&amp;text={{../../title}}" class="twitter">
              {{> svg/social/twitter}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'google_plusone'}}
          <li class="social-link google-plus">
            <a target="_blank" href="https://plus.google.com/share?url={{../../url}}" class="google">
              {{> svg/social/googleplus}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'linkedin'}}
          <li class="social-link linkedin">
            <a target="_blank" href="http://www.linkedin.com/shareArticle?url={{../../url}}&amp;title={{../../title}}" class="linkedin">
              {{> svg/social/linkedin}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'email'}}
          <li class="social-link mail">
            <a href="mailto:?subject={{../../title}}&amp;body={{../../url}}" class="email">
              {{> svg/social/email}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'print'}}
          <li class="social-link print">
            <a class="print" href="javascript:;" onclick="window.print()">
              {{> svg/social/print}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'pinterest'}}
          <li class="social-link pinterest">
            <a target="_blank" href="//pinterest.com/pin/create/button/?url={{../../url}}&amp;media={{../../image}}&amp;description={{../../title}}" class="share-pinterest">
              {{> svg/social/pinterest}}
            </a>
          </li>
        {{/if}}
      {{/each}}

    </ul>
  </nav>
{{/if}}

core/products/price-range:
{{!
  Shows a product's price range.

  @param price_range object

  @param show_amp_classes bool
    If true include amp class component
}}
<div
  class="
    price
    {{#if show_amp_classes}}
      {{> core/amp/common/price-class}}
    {{/if}}
  "
>
  {{#and price_range.min.with_tax price_range.max.with_tax}}
    <div class="price-withtax" data-product-price-wrapper="with-tax">
      <span data-product-price-with-tax class="price price-value">
        {{price_range.min.with_tax.formatted}} - {{price_range.max.with_tax.formatted}}
      </span>
      {{#and price_range.min.without_tax price_range.max.without_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.with_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}
  {{#and price_range.min.without_tax price_range.max.without_tax}}
    <div class="price-withouttax" data-product-price-wrapper="without-tax">
      <span data-product-price-without-tax class="price price-without-tax">
        {{price_range.min.without_tax.formatted}} - {{price_range.max.without_tax.formatted}}
      </span>
      {{#and price_range.min.with_tax price_range.max.with_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.without_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}
</div>

core/products/sale-badge:
{{!
  Shows a sale badge if a sale price has been entered

  Themes can use this to display a sale badge.

  @param classes string
    Class(es) to append to sale badge
}}
{{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
  <span class="sale-badge {{classes}}">
    {{lang 'core.product.sale'}}
  </span>
{{/or}}

core/amp/common/price-class:
{{#if price.with_tax}}
  {{#unless price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-taxed-onsale
    {{else}}
      prices-taxed
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.without_tax}}
  {{#unless price.with_tax}}
    {{#if price.rrp_without_tax}}
      prices-notax-onsale
    {{else}}
      prices-notax
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.with_tax}}
  {{#if price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-both-onsale
    {{else}}
      prices-both
    {{/if}}
  {{/if}}
{{/if}}

components/products/bulk-pricing:
{{#if product.bulk_discount_rates.length}}
  <div class="bulk-pricing-block">
    <h1 class="product-details-heading">
      {{lang 'product.bulk_pricing.title'}}
    </h1>
    <ul class="bulk-pricing-list">
      <li class="bulk-pricing-item bulk-pricing-item-title">
        <span class="bulk-pricing-range">
          {{lang 'product.bulk_pricing.qty'}}
        </span>
        <span class="bulk-pricing-value">
          {{#each (limit product.bulk_discount_rates 1)}}
            {{#if type '===' 'fixed'}}
              {{lang 'product.bulk_pricing.price_per_unit'}}
            {{else}}
              {{lang 'product.bulk_pricing.discount'}}
            {{/if}}
          {{/each}}
        </span>
      </li>
      {{#each product.bulk_discount_rates}}
        <li class="bulk-pricing-item">
          <span class="bulk-pricing-range">
            {{lang 'product.bulk_pricing.range' min=min max=max}}
          </span>
          <span class="bulk-pricing-value">
            {{lang 'product.bulk_pricing.discount_formatted' discount=discount.formatted}}
          </span>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}
