components/amp/styles/mini-cart:
.button-cart {
  position: relative;
  background: transparent;
  border: 0;
}

.icon-cart {
  width: 21px;
  height: 18px;
  fill: {{color_text}};
}

.icon-cart-count {
  position: absolute;
  top: -20px;
  left: 12px;
  display: block;
  width: 0;
  height: 0;
  font-size: 9px;
  color: #fff;
  border: 12px solid transparent;
  border-bottom-color: {{color_accent}};
  transform: scale(0);
  transition: transform 0.5s ease;
}

.icon-cart-count:after {
  position: absolute;
  top: 12px;
  left: -12px;
  width: 0;
  height: 0;
  border: 12px solid transparent;
  border-top-color: {{color_accent}};
  content: "";
}

.icon-cart-count.show {
  transform: scale(1);
}

.icon-cart-count .number {
  position: absolute;
  z-index: 1;
  top: 12px;
  left: 50%;
  line-height: 0;
  transform: translateX(-50%);
}
