components/product-catalog/grid:
{{!--
  Product Grid

  @param products
    The list of product items to loop over / display.
--}}
{{> components/mobile-filters/mobile-filters}}
{{> components/product-catalog/listing-utils}}
<div
  class="product-listing-grid grid"
  data-category
  {{#if catalog_type.show_compare}}
    data-product-compare
  {{/if}}
>
  {{#if catalog_type.products.length}}
    <div class="listing-wrapper">
      {{#each catalog_type.products}}
        {{> components/product-catalog/product-item
          catalog_view=../catalog_view
          customer=../customer
          show_rating=../settings.show_product_rating
          show_compare=../catalog_type.show_compare
          image_ratio=../theme_settings.product_grid_image_ratio
          default_image=../theme_settings.default_image
          quick_shop=../theme_settings.quick_shop
          hide_brand=../theme_settings.product_brand_hidden
          hide_price=../theme_settings.restrict_to_login
          price_range_display=../theme_settings.price_range_display
          retail_price_label=../theme_settings.retail-price-label
          non_sale_price_label=../theme_settings.non-sale-price-label
          sale_price_label=../theme_settings.sale-price-label
          price_label=../theme_settings.price-label
        }}
      {{/each}}
    </div>
  {{else}}
    <h3 class="listing-no-products">
      {{lang 'common.no_products'}}
    </h3>
  {{/if}}

  {{> components/common/pagination pagination}}
</div>

components/product-catalog/product-item:
{{!--
  Product Item

  Used in both grid and list layouts

  @param collection_view theme-setting/JS
    Theme setting and toggle on catalog pages to switch between grid/list views

  @param show_rating platform-setting
    Platform setting to toggle ratings

  @param show_compare bool
    Whether to show "compare" checkboxes

  @param default_image theme-setting
    The default image if none exists

  @param as_low_as theme-setting
    The setting to show/hide as low as price wording

  @param quick_shop theme-setting
    Theme setting to enable / disable quickshop
--}}

<article
  class="product-{{catalog_view}}-item product-block"
  data-product-title="{{name}}"
  data-product-id="{{id}}"
>
  <div class="product-thumb-wrapper">
    <figure class="product-item-thumbnail">
      {{#if price.saved}}
        <div class="product-badge sale">
          <span class="message">{{lang 'product.sale'}}</span>
        </div>
      {{/if}}
      {{#if out_of_stock_message}}
        <div class="product-badge sold-out">
          <span class="message">{{out_of_stock_message}}</span>
        </div>
      {{/if}}
      <a href="{{url}}"
        data-product-href="{{url}}"
      >
        {{> components/common/image
          img=image
          url=false
          size="thumb"
          default=default_image
          image_ratio=image_ratio
          not_lazy=not_lazy
        }}
        <div class="product-item-mask">
          <div class="product-item-actions product-item-{{catalog_view}}-actions">
            {{> components/product-catalog/product-item-actions
              product_grid=true
            }}
          </div>
        </div>
      </a>
    </figure>
  </div>

  <div class="product-item-details">
    {{#if show_rating}}
      <p class="product-item-rating">
        {{> components/products/ratings rating=rating}}
      </p>
    {{/if}}

    {{#unless hide_brand}}
      {{#if brand.name}}
        <div class="product-item-brand">{{brand.name}}</div>
      {{/if}}
    {{/unless}}

    <h3 class="product-item-title">
      <a href="{{{url}}}">{{name}}</a>
    </h3>
    <div
      class="product-item-price"
      data-product-price="{{price.without_tax.formatted}}"
    >
      {{#or customer (unless hide_price)}}
        {{> core/products/price
          price=price
          show_tax_labels=true
          show_sale_badges=false
          price_range_display=price_range_display
          retail_price_label=retail_price_label
          non_sale_price_label=non_sale_price_label
          sale_price_label=sale_price_label
          price_label=price_label
        }}
      {{/or}}
    </div>

    <div class="product-description">
      {{{summary}}}
    </div>

    <div class="product-item-actions product-item-{{catalog_view}}-actions">
      {{> components/product-catalog/product-item-actions
        product_grid=false
      }}
    </div>

    {{> components/product-catalog/product-item-quick-actions
      class="below-product-item"
      hide_price=hide_price
    }}

  </div>
</article>

