components/mobile-filters/facets:
{{#each catalog_type.facets}}
  {{#if visible}}
    {{#if items}}
      <div class="facet-list {{display}} mobile-facet-list" data-facet-filter>

        {{> components/common/facet-toggle title=title}}

        {{{dynamicComponent 'components/faceted-search/facets'}}}

        {{#if remove_url}}
          <a
            class="link facet-remove"
            href="{{remove_url}}"
            data-faceted-search-facet
            data-facet-remove
          >
            {{lang 'faceted_search.clear'}}
          </a>
        {{/if}}
      </div>
    {{/if}}

    {{#compare facet 'price'}}
      <div class="facet-list {{display}} mobile-facet-list" data-facet-filter>

        {{> components/common/facet-toggle title=title}}

        {{{dynamicComponent 'components/faceted-search/facets'}}}

        {{#if remove_url}}
          <a
            class="link facet-remove"
            href="{{remove_url}}"
            data-faceted-search-facet
            data-facet-remove
          >
            {{lang 'faceted_search.clear'}}
          </a>
        {{/if}}
      </div>
    {{/compare}}
  {{/if}}
{{/each}}

components/product-catalog/subcategory-b:
{{!
    @param list
           Brands / Categories

    @param title
           List title

}}

{{#if list}}
  <div class="category-nav facet-list" data-facet-filter>
    {{> components/common/facet-toggle title=title}}
    <ul class="category-nav-list facet-list-items is-open" data-facet-filter-wrapper>
      {{#each list}}
        <li class="facet-item">
          <a href="{{url}}">
            {{name}} {{#if ../arrow}}{{> svg/arrow-right}}{{/if}}
          </a>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}

components/product-catalog/shop-by-price:
{{#if shop_by_price}}
  <div class="sidebar-block shop-by-price facet-list" data-facet-filter>
    {{> components/common/facet-toggle title=(lang 'category.shop_by_price')}}
    <ul class="facet-list-items is-open" data-facet-filter-wrapper>
      {{#each shop_by_price}}
        <li class="facet-item">
          <a
            class="facet-item-link {{#if selected }}is-active{{/if}}"
            href="{{url}}"
            alt="{{low.formatted}} - {{high.formatted}}"
          >
            {{low.formatted}} - {{high.formatted}}
          </a>
        </li>
      {{/each}}
    </ul>
    {{#any shop_by_price selected=true}}
      <a href="{{category_url}}" class="link facet-remove price-facet-remove">
        {{lang 'category.reset'}}
      </a>
    {{/any}}
  </div>
{{/if}}

components/common/facet-toggle:
{{!
  @param title
         The title of the faceted list to toggle.
}}

<h5 class="facet-list-title {{#unless start_collapsed}}is-open{{/unless}}" data-facet-toggle>
  {{title}}
  <span class="facet-list-toggle">
    <svg class="icon icon-chevron-down"><use xlink:href="#icon-chevron-down" /></svg>
  </span>
</h5>
