components/navigation/main-navigation-li:
{{#each navigation}}
  {{#if children}}
    <li
      class="
        main-nav-item
        dropdown
        {{#if ../../style '==' 'dropdown-standard'}}
          pages-dropdown
        {{/if}}
        {{#if ../../style '==' 'dropdown-meganav'}}
          mega-nav
        {{/if}}
        {{> components/navigation/main-navigation-classes
          style=../../style
        }}
      "
      data-dropdown
    >
      <a
        class="dropdown-toggle top-level-nav-link items"
        href="#"
        data-dropdown-toggle
      >
        {{name}}
        <svg class="icon icon-arrow-down">
          <use xlink:href="#icon-arrow-down" />
        </svg>
      </a>
      {{#if ../../style '==' 'dropdown-meganav'}}
        {{> components/navigation/mega-navigation
          full_width=../../../theme_settings.use-fullwidth
          loop_item=children
          show_images=../../../show_images
        }}
      {{else}}
        <ul
          class="
            dropdown-panel
            {{#if ../../../style '==' 'dropdown-standard'}}
              pages-panel
            {{/if}}
          "
          data-dropdown-panel
          {{#unless ../../../style '==' 'dropdown-standard'}}
            {{#if ../../../children.length '>=' 2}}
              data-position-navigation
            {{/if}}
          {{/unless}}
        >
          {{#if ../../../style '==' 'dropdown-expanded'}}
            <div
              class="
                dropdown-expanded-panel
                {{#unless ../../../../theme_settings.use-fullwidth}}
                  contained-width
                {{/unless}}
              "
            >
          {{/if}}
          {{> components/navigation/navigation-item-loop
            show_all=../../../show_all
            show_images=../../../show_images
          }}
          {{#if ../../../style '==' 'dropdown-expanded'}}
            </div>
          {{/if}}
        </ul>
      {{/if}}
    </li>
  {{else}}
    <li class="main-nav-item">
      <a class="top-level-nav-link items" href="{{url}}">
        {{name}}
      </a>
    </li>
  {{/if}}
{{/each}}

components/header/customer-utils:
{{> components/header/compare-list}}

{{> components/header/currency-selector}}

{{#if settings.account_creation_enabled}}
  <div class="customer-links customer-util">
    {{> components/header/account-button}}
  </div>
{{/if}}

{{#if settings.show_wishlist}}
  {{#if customer}}
    <a class="top-bar-button button-wishlist customer-util" href="{{urls.account.wishlists.all}}">
      <svg class="icon icon-wishlist"><use xlink:href="#icon-wishlist" /></svg>
    </a>
  {{/if}}
{{/if}}

<div class="mini-cart-wrap dropdown customer-util logo-align-center" data-dropdown>
  {{> components/mini-cart/mini-cart }}
</div>

components/navigation/main-navigation-cl:
{{#if navigation '==' categories}}
  {{#if style '==' 'dropdown-expanded'}}
    {{#if children.length '==' 1}}
      two-children
    {{/if}}
    {{#if children.length '==' 2}}
      three-children
    {{/if}}
    {{#if children.length '==' 3}}
      four-children
    {{/if}}
    {{#if children.length '==' 4}}
      five-children
    {{/if}}
    {{#if children.length '>=' 5}}
      six-children
    {{/if}}
  {{/if}}
{{/if}}

components/navigation/shop-navigation-cl:
{{#if style '==' 'dropdown-expanded'}}
  {{#if categories.length '==' 2}}
    two-children
  {{/if}}
  {{#if categories.length '==' 3}}
    three-children
  {{/if}}
  {{#if categories.length '==' 4}}
    four-children
  {{/if}}
  {{#if categories.length '==' 5}}
    five-children
  {{/if}}
  {{#if categories.length '>=' 6}}
    six-children
  {{/if}}
{{/if}}

components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector customer-util" data-currency-selector>
    <span class="form-selected-text" data-currency-selector-toggle>
      {{currency_selector.active_currency_code}}
    </span>
    <i class="icon icon-arrow-down"></i>
    <select class="currencies">
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}} data-value="{{currency-code}}">
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}

components/navigation/mega-navigation-lo:
{{#each children}}
  {{#if children}}
    <div
      class="navigation-child-list-container"
      data-parent-category-id="{{id}}"
      data-parent-category-name="{{name}}"
    >
      <h2 class="navigation-child-list-header">
        {{name}}
      </h2>
      <div class="navigation-child-list-inner">
        <ul class="navigation-child-list">
          <li class="navigation-child-item">
            <a href="{{url}}">
              {{lang 'common.navigation_all' name=name}}
            </a>
          </li>
          {{#each children}}
            {{#if children}}
              <li class="navigation-child-item">
                <a href="#">{{name}}</a>
                <ul class="dropdown-tier">
                  <li class="parent-all nav-tier-item">
                    <a href="{{url}}">
                      {{lang 'common.navigation_all' name=name}}
                    </a>
                  </li>
                  {{#each children}}
                    {{#if children}}
                      <li
                        class="nav-tier-item has-children"
                        data-child-category-id="{{id}}"
                        data-child-category-name="{{name}}"
                        data-find-child
                      >
                        <a href="{{url}}">
                          {{name}}
                          <svg class="icon icon-arrow-down">
                            <use xlink:href="#icon-arrow-down" />
                          </svg>
                        </a>
                      </li>
                    {{else}}
                      <li class="nav-tier-item">
                        <a href="{{url}}">
                          {{name}}
                        </a>
                      </li>
                    {{/if}}
                  {{/each}}
                </ul>
              </li>
            {{else}}
              <li class="navigation-child-item">
                <a href="{{url}}">{{name}}</a>
              </li>
            {{/if}}
          {{/each}}
          {{#for 1 3}}
            <li class="navigation-child-item-empty"></li>
          {{/for}}
        </ul>
      </div>
    </div>
    {{> components/navigation/mega-navigation-loop}}
  {{/if}}
{{/each}}
